// src/App.js
import React, { useRef, useState } from 'react';
import { PDFDocument } from 'pdf-lib';
import { Document, Page, pdfjs } from 'react-pdf';
import SignatureCanvas from 'react-signature-canvas';

// Fix for PDF worker setup
pdfjs.GlobalWorkerOptions.workerSrc = `${process.env.PUBLIC_URL}/pdf.worker.min.js`;

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [numPages, setNumPages] = useState(0);
  const [signatureData, setSignatureData] = useState(null);
  const sigCanvas = useRef();

  const handleFile = (e) => setPdfFile(e.target.files[0]);

  const handleSaveSignature = () => {
    if (sigCanvas.current && !sigCanvas.current.isEmpty()) {
      const canvas = sigCanvas.current.getCanvas();
      setSignatureData(canvas.toDataURL('image/png'));
      alert('Signature saved!');
    } else {
      alert('Draw your signature first!');
    }
  };

  const handleExport = async () => {
    if (!pdfFile || !signatureData) {
      alert('Select a PDF and draw a signature first!');
      return;
    }

    // Load original PDF
    const arrayBuffer = await pdfFile.arrayBuffer();
    const pdfDoc = await PDFDocument.load(arrayBuffer);

    // Embed signature as PNG
    const pngImage = await pdfDoc.embedPng(signatureData);

    // Draw signature on first page (adjust x/y/width/height as needed)
    const page = pdfDoc.getPage(0);
    const { width, height } = page.getSize();
    page.drawImage(pngImage, {
      x: 50,
      y: height - 150,
      width: 200,
      height: 100,
    });

    // Save and trigger download
    const pdfBytes = await pdfDoc.save();
    const blob = new Blob([pdfBytes], { type: 'application/pdf' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'signed.pdf';
    link.click();
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>PDF Signer</h1>

      <input type="file" accept="application/pdf" onChange={handleFile} />

      {pdfFile && (
        <div>
          <Document
            file={pdfFile}
            onLoadSuccess={({ numPages }) => setNumPages(numPages)}
          >
            {Array.from({ length: numPages }, (_, index) => (
              <Page key={index} pageNumber={index + 1} width={600} />
            ))}
          </Document>

          <div style={{ border: '1px solid #ccc', marginTop: 10 }}>
            <SignatureCanvas
              ref={sigCanvas}
              penColor="black"
              canvasProps={{ width: 600, height: 200, className: 'sigCanvas' }}
            />
            <button onClick={handleSaveSignature}>Save Signature</button>
          </div>

          <button onClick={handleExport} style={{ marginTop: 10 }}>
            Export Signed PDF
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
