// src/App.js
import React, { useRef, useState } from 'react';
import { PDFDocument } from 'pdf-lib';
import { Document, Page, pdfjs } from 'react-pdf';
import SignatureCanvas from 'react-signature-canvas';
import './App.css';

// Fix for PDF worker setup
pdfjs.GlobalWorkerOptions.workerSrc = `${process.env.PUBLIC_URL}/pdf.worker.min.js`;

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [numPages, setNumPages] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [signatures, setSignatures] = useState({});
  const [signingMode, setSigningMode] = useState(false);
  const [pageDimensions, setPageDimensions] = useState({});
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const sigCanvas = useRef();
  const pageContainerRef = useRef();
  const signatureOverlayRef = useRef();

  const handleFile = (e) => {
    setPdfFile(e.target.files[0]);
    setCurrentPage(1);
    setSignatures({});
    setSigningMode(false);
    setZoom(1);
  };

  const handlePageLoadSuccess = ({ width, height }) => {
    setPageDimensions(prev => ({
      ...prev,
      [currentPage]: { width, height }
    }));
  };

  const handleSaveSignature = () => {
    if (sigCanvas.current && !sigCanvas.current.isEmpty()) {
      const canvas = sigCanvas.current.getCanvas();
      const newSignatures = { ...signatures };
      newSignatures[currentPage] = {
        data: canvas.toDataURL('image/png'),
        x: 50,
        y: 50,
        width: 150,
        height: 75,
      };
      setSignatures(newSignatures);
      sigCanvas.current.clear();
      setSigningMode(false);
      alert('Signature saved to page ' + currentPage);
    } else {
      alert('Draw your signature first!');
    }
  };

  const handleClearSignature = () => {
    if (sigCanvas.current) {
      sigCanvas.current.clear();
    }
  };

  const handleClearPageSignature = () => {
    const newSignatures = { ...signatures };
    delete newSignatures[currentPage];
    setSignatures(newSignatures);
    sigCanvas.current?.clear();
  };

  const handleMouseDown = (e) => {
    if (!signatures[currentPage]) return;
    setIsDragging(true);
    const rect = pageContainerRef.current.getBoundingClientRect();
    const startX = e.clientX - rect.left;
    const startY = e.clientY - rect.top;
    const sig = signatures[currentPage];
    setDragOffset({
      startX,
      startY,
      initX: sig.x,
      initY: sig.y,
    });
  };

  const handleMouseMove = (e) => {
    if (!isDragging || !signatures[currentPage]) return;

    const rect = pageContainerRef.current.getBoundingClientRect();
    const currentX = e.clientX - rect.left;
    const currentY = e.clientY - rect.top;

    const deltaX = currentX - dragOffset.startX;
    const deltaY = currentY - dragOffset.startY;

    const newX = Math.max(0, dragOffset.initX + deltaX);
    const newY = Math.max(0, dragOffset.initY + deltaY);

    const newSignatures = { ...signatures };
    newSignatures[currentPage] = {
      ...newSignatures[currentPage],
      x: newX,
      y: newY,
    };
    setSignatures(newSignatures);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleTouchStart = (e) => {
    if (!signatures[currentPage]) return;
    setIsDragging(true);
    const rect = pageContainerRef.current.getBoundingClientRect();
    const touch = e.touches[0];
    const startX = touch.clientX - rect.left;
    const startY = touch.clientY - rect.top;
    const sig = signatures[currentPage];
    setDragOffset({
      startX,
      startY,
      initX: sig.x,
      initY: sig.y,
    });
  };

  const handleTouchMove = (e) => {
    if (!isDragging || !signatures[currentPage]) return;

    const rect = pageContainerRef.current.getBoundingClientRect();
    const touch = e.touches[0];
    const currentX = touch.clientX - rect.left;
    const currentY = touch.clientY - rect.top;

    const deltaX = currentX - dragOffset.startX;
    const deltaY = currentY - dragOffset.startY;

    const newX = Math.max(0, dragOffset.initX + deltaX);
    const newY = Math.max(0, dragOffset.initY + deltaY);

    const newSignatures = { ...signatures };
    newSignatures[currentPage] = {
      ...newSignatures[currentPage],
      x: newX,
      y: newY,
    };
    setSignatures(newSignatures);
  };

  const handleTouchEnd = () => {
    setIsDragging(false);
  };

  const handleExport = async () => {
    if (!pdfFile || Object.keys(signatures).length === 0) {
      alert('Select a PDF and add at least one signature!');
      return;
    }

    try {
      const arrayBuffer = await pdfFile.arrayBuffer();
      const pdfDoc = await PDFDocument.load(arrayBuffer);

      // Add signatures to all pages that have them
      for (const [pageNum, sigObj] of Object.entries(signatures)) {
        const pageIndex = parseInt(pageNum) - 1;
        if (pageIndex >= 0 && pageIndex < pdfDoc.getPageCount()) {
          const page = pdfDoc.getPage(pageIndex);
          const pngImage = await pdfDoc.embedPng(sigObj.data);

          page.drawImage(pngImage, {
            x: sigObj.x,
            y: page.getHeight() - sigObj.y - 75,
            width: 150,
            height: 75,
          });
        }
      }

      const pdfBytes = await pdfDoc.save();
      const blob = new Blob([pdfBytes], { type: 'application/pdf' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'signed.pdf';
      link.click();
    } catch (error) {
      alert('Error exporting PDF: ' + error.message);
    }
  };

  const pageWidth = typeof window !== 'undefined' ? Math.min(window.innerWidth - 40, 600) : 600;

  return (
    <div className="app-container">
      <div className="header">
        <h1>üìÑ PDF Signer</h1>
        <p>Sign your documents and drag to position</p>
      </div>

      <div className="file-input-section">
        <input 
          type="file" 
          accept="application/pdf" 
          onChange={handleFile}
          className="file-input"
        />
      </div>

      {pdfFile ? (
        <div className="main-content">
          <div className="pdf-viewer-section">
            <Document
              file={pdfFile}
              onLoadSuccess={({ numPages }) => setNumPages(numPages)}
              loading={<div className="loading">Loading PDF...</div>}
              error={<div className="error">Error loading PDF</div>}
            >
              <div 
                ref={pageContainerRef}
                className="page-container"
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
                onMouseLeave={handleMouseUp}
                onTouchStart={handleTouchStart}
                onTouchMove={handleTouchMove}
                onTouchEnd={handleTouchEnd}
              >
                <div className="pdf-wrapper">
                  <Page 
                    pageNumber={currentPage} 
                    width={pageWidth}
                    renderTextLayer={false}
                    renderAnnotationLayer={false}
                    onLoadSuccess={handlePageLoadSuccess}
                  />

                  {/* Signature Overlay */}
                  {signatures[currentPage] && (
                    <div
                      ref={signatureOverlayRef}
                      className="signature-overlay"
                      style={{
                        left: `${signatures[currentPage].x}px`,
                        top: `${signatures[currentPage].y}px`,
                        cursor: isDragging ? 'grabbing' : 'grab',
                      }}
                    >
                      <img 
                        src={signatures[currentPage].data}
                        alt="Signature"
                        className="overlay-image"
                        draggable={false}
                      />
                      <div className="drag-hint">Drag to move</div>
                    </div>
                  )}
                </div>
              </div>
            </Document>

            <div className="page-controls">
              <button 
                onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                disabled={currentPage <= 1}
                className="nav-button"
              >
                ‚Üê Previous
              </button>
              <span className="page-info">
                Page {currentPage} of {numPages}
              </span>
              <button 
                onClick={() => setCurrentPage(Math.min(numPages, currentPage + 1))}
                disabled={currentPage >= numPages}
                className="nav-button"
              >
                Next ‚Üí
              </button>
            </div>

            {signatures[currentPage] && (
              <div className="signature-indicator">
                ‚úì Signature on this page (Drag to reposition)
              </div>
            )}
          </div>

          <div className="signing-section">
            <div className="section-header">
              <h2>Sign Page {currentPage}</h2>
              <button 
                onClick={() => setSigningMode(!signingMode)}
                className={`toggle-button ${signingMode ? 'active' : ''}`}
              >
                {signingMode ? '‚úèÔ∏è Signing ON' : '‚úèÔ∏è Signing OFF'}
              </button>
            </div>

            {signingMode && (
              <div className="signature-canvas-wrapper">
                <p className="canvas-label">Draw your signature below:</p>
                <SignatureCanvas
                  ref={sigCanvas}
                  penColor="black"
                  canvasProps={{ 
                    width: pageWidth - 20, 
                    height: 200, 
                    className: 'sig-canvas' 
                  }}
                />

                <div className="button-group">
                  <button onClick={handleClearSignature} className="secondary-button">
                    Clear Canvas
                  </button>
                  <button onClick={handleSaveSignature} className="primary-button">
                    Save Signature
                  </button>
                </div>
              </div>
            )}

            {signatures[currentPage] && (
              <div className="signature-preview">
                <p className="preview-label">Signature on PDF:</p>
                <p className="position-info">
                  Position: X: {Math.round(signatures[currentPage].x)}px, Y: {Math.round(signatures[currentPage].y)}px
                </p>
                <p className="hint-text">üëÜ Go to page {currentPage} and drag on the PDF to move the signature</p>
                <button 
                  onClick={handleClearPageSignature} 
                  className="danger-button"
                >
                  Remove Signature
                </button>
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className="empty-state">
          <p>üìÅ Upload a PDF file to get started</p>
        </div>
      )}

      {pdfFile && Object.keys(signatures).length > 0 && (
        <div className="export-section">
          <div className="signatures-summary">
            <strong>Signatures ready on {Object.keys(signatures).length} page(s)</strong>
          </div>
          <button onClick={handleExport} className="export-button">
            ‚¨áÔ∏è Export Signed PDF
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
