// src/App.js
import React, { useRef, useState } from 'react';
import { PDFDocument } from 'pdf-lib';
import { Document, Page, pdfjs } from 'react-pdf';
import SignatureCanvas from 'react-signature-canvas';
import './App.css';

// Fix for PDF worker setup
pdfjs.GlobalWorkerOptions.workerSrc = `${process.env.PUBLIC_URL}/pdf.worker.min.js`;

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [numPages, setNumPages] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [signatures, setSignatures] = useState({});
  const [signingMode, setSigningMode] = useState(false);
  const [pageDimensions, setPageDimensions] = useState({});
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const sigCanvas = useRef();
  const pageContainerRef = useRef();
  const signatureOverlayRef = useRef();

  const handleFile = (e) => {
    setPdfFile(e.target.files[0]);
    setCurrentPage(1);
    setSignatures({});
    setSigningMode(false);
    setZoom(1);
  };

  const handlePageLoadSuccess = ({ width, height }) => {
    setPageDimensions(prev => ({
      ...prev,
      [currentPage]: { width, height }
    }));
  };

  const handleSaveSignature = () => {
    if (sigCanvas.current && !sigCanvas.current.isEmpty()) {
      const canvas = sigCanvas.current.getCanvas();
      const newSignatures = { ...signatures };
      newSignatures[currentPage] = {
        data: canvas.toDataURL('image/png'),
        x: 50,
        y: 50,
        width: 150,
        height: 75,
      };
      setSignatures(newSignatures);
      sigCanvas.current.clear();
      setSigningMode(false);
      alert('Signature saved to page ' + currentPage);
    } else {
      alert('Draw your signature first!');
    }
  };

  const handleClearSignature = () => {
    if (sigCanvas.current) {
      sigCanvas.current.clear();
    }
  };

  const handleClearPageSignature = () => {
    const newSignatures = { ...signatures };
    delete newSignatures[currentPage];
    setSignatures(newSignatures);
    sigCanvas.current?.clear();
  };

  const handleMouseDown = (e) => {
    if (!signatures[currentPage]) return;
    setIsDragging(true);
    const rect = pageContainerRef.current.getBoundingClientRect();
    const startX = e.clientX - rect.left;
    const startY = e.clientY - rect.top;
    const sig = signatures[currentPage];
    setDragOffset({
      startX,
      startY,
      initX: sig.x,
      initY: sig.y,
    });
  };

  const handleMouseMove = (e) => {
    if (!isDragging || !signatures[currentPage]) return;

    const rect = pageContainerRef.current.getBoundingClientRect();
    const currentX = e.clientX - rect.left;
    const currentY = e.clientY - rect.top;

    const deltaX = currentX - dragOffset.startX;
    const deltaY = currentY - dragOffset.startY;

    const newX = Math.max(0, dragOffset.initX + deltaX);
    const newY = Math.max(0, dragOffset.initY + deltaY);

    const newSignatures = { ...signatures };
    newSignatures[currentPage] = {
      ...newSignatures[currentPage],
      x: newX,
      y: newY,
    };
    setSignatures(newSignatures);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleTouchStart = (e) => {
    if (!signatures[currentPage]) return;
    setIsDragging(true);
    const rect = pageContainerRef.current.getBoundingClientRect();
    const touch = e.touches[0];
    const startX = touch.clientX - rect.left;
    const startY = touch.clientY - rect.top;
    const sig = signatures[currentPage];
    setDragOffset({
      startX,
      startY,
      initX: sig.x,
      initY: sig.y,
    });
  };

  const handleTouchMove = (e) => {
    if (!isDragging || !signatures[currentPage]) return;

    const rect = pageContainerRef.current.getBoundingClientRect();
    const touch = e.touches[0];
    const currentX = touch.clientX - rect.left;
    const currentY = touch.clientY - rect.top;

    const deltaX = currentX - dragOffset.startX;
    const deltaY = currentY - dragOffset.startY;

    const newX = Math.max(0, dragOffset.initX + deltaX);
    const newY = Math.max(0, dragOffset.initY + deltaY);

    const newSignatures = { ...signatures };
    newSignatures[currentPage] = {
      ...newSignatures[currentPage],
      x: newX,
      y: newY,
    };
    setSignatures(newSignatures);
  };

  const handleTouchEnd = () => {
    setIsDragging(false);
  };

  const handleExport = async () => {
    if (!pdfFile || Object.keys(signatures).length === 0) {
      alert('Select a PDF and add at least one signature!');
      return;
    }

    try {
      const arrayBuffer = await pdfFile.arrayBuffer();
      const pdfDoc = await PDFDocument.load(arrayBuffer);

      // Add signatures to all pages that have them
      for (const [pageNum, sigObj] of Object.entries(signatures)) {
        const pageIndex = parseInt(pageNum) - 1;
        if (pageIndex >= 0 && pageIndex < pdfDoc.getPageCount()) {
          const page = pdfDoc.getPage(pageIndex);
          const pngImage = await pdfDoc.embedPng(sigObj.data);
          const pageHeight = page.getHeight();
          const pageWidth = page.getWidth();

          // Convert screen coordinates to PDF coordinates
          // The signature position on screen is relative to the rendered page
          // We need to scale it based on the page dimensions
          const displayWidth = pageDimensions[pageNum]?.width || 600;
          const scaleX = pageWidth / displayWidth;
          const scaleY = pageHeight / (displayWidth * pageHeight / pageWidth);

          page.drawImage(pngImage, {
            x: sigObj.x * scaleX,
            y: pageHeight - (sigObj.y * scaleY) - (sigObj.height || 75),
            width: (sigObj.width || 150) * scaleX,
            height: (sigObj.height || 75) * scaleY,
          });
        }
      }

      const pdfBytes = await pdfDoc.save();
      const blob = new Blob([pdfBytes], { type: 'application/pdf' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'signed.pdf';
      link.click();
    } catch (error) {
      alert('Error exporting PDF: ' + error.message);
    }
  };

  const pageWidth = typeof window !== 'undefined' ? Math.min(window.innerWidth - 40, 600) : 600;

  return (
    <div className="app-container">
      <div className="app-header">
        <h1 className="app-title">üìÑ DocSign</h1>
        <p className="app-subtitle">Professional PDF Signing</p>
      </div>

      {!pdfFile ? (
        <div className="onboarding-screen">
          <div className="onboarding-content">
            <div className="onboarding-icon">üìã</div>
            <h2>Welcome to DocSign</h2>
            <p>Upload a PDF to get started with signing</p>
            <label className="file-input-label">
              <input 
                type="file" 
                accept="application/pdf" 
                onChange={handleFile}
                className="file-input-hidden"
              />
              <span className="file-button">Choose PDF File</span>
            </label>
          </div>
        </div>
      ) : (
        <div className="main-content">
          <div className="pdf-viewer-section">
            <div className="zoom-controls">
              <button 
                onClick={() => setZoom(Math.max(0.5, zoom - 0.2))}
                className="zoom-button"
                title="Zoom Out"
              >
                ‚àí
              </button>
              <span className="zoom-level">{Math.round(zoom * 100)}%</span>
              <button 
                onClick={() => setZoom(Math.min(2, zoom + 0.2))}
                className="zoom-button"
                title="Zoom In"
              >
                +
              </button>
              <button 
                onClick={() => setZoom(1)}
                className="zoom-button reset"
                title="Reset Zoom"
              >
                ‚Üª
              </button>
            </div>

            <div className="page-indicator">
              <span className="page-badge">{currentPage}</span>
              <span className="page-text">of {numPages}</span>
            </div>

            <Document
              file={pdfFile}
              onLoadSuccess={({ numPages }) => setNumPages(numPages)}
              loading={<div className="loading">üìñ Loading PDF...</div>}
              error={<div className="error">‚ö†Ô∏è Error loading PDF</div>}
            >
              <div 
                ref={pageContainerRef}
                className="page-container"
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
                onMouseLeave={handleMouseUp}
                onTouchStart={handleTouchStart}
                onTouchMove={handleTouchMove}
                onTouchEnd={handleTouchEnd}
              >
                <div className="pdf-wrapper" style={{ transform: `scale(${zoom})`, transformOrigin: 'top center' }}>
                  <Page 
                    pageNumber={currentPage} 
                    width={pageWidth}
                    renderTextLayer={false}
                    renderAnnotationLayer={false}
                    onLoadSuccess={handlePageLoadSuccess}
                  />

                  {/* Signature Overlay */}
                  {signatures[currentPage] && (
                    <div
                      ref={signatureOverlayRef}
                      className="signature-overlay"
                      style={{
                        left: `${signatures[currentPage].x}px`,
                        top: `${signatures[currentPage].y}px`,
                        cursor: isDragging ? 'grabbing' : 'grab',
                      }}
                    >
                      <img 
                        src={signatures[currentPage].data}
                        alt="Signature"
                        className="overlay-image"
                        draggable={false}
                      />
                      <div className="drag-hint">‚Üî Drag</div>
                    </div>
                  )}
                </div>
              </div>
            </Document>

            <div className="page-navigation">
              <button 
                onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                disabled={currentPage <= 1}
                className="nav-button-large"
              >
                ‚Üê Back
              </button>
              <button 
                onClick={() => setCurrentPage(Math.min(numPages, currentPage + 1))}
                disabled={currentPage >= numPages}
                className="nav-button-large"
              >
                Next ‚Üí
              </button>
            </div>

            {signatures[currentPage] && (
              <div className="signature-status">
                <div className="status-badge">‚úì Signed</div>
              </div>
            )}
          </div>

          <div className="signing-panel">
            <div className="panel-header">
              <h2>Signature</h2>
              <button 
                onClick={() => setSigningMode(!signingMode)}
                className={`signing-toggle ${signingMode ? 'active' : ''}`}
              >
                {signingMode ? '‚úé Drawing' : '‚úé Ready'}
              </button>
            </div>

            {signingMode ? (
              <div className="signing-canvas-container">
                <SignatureCanvas
                  ref={sigCanvas}
                  penColor="black"
                  canvasProps={{ 
                    width: pageWidth - 20, 
                    height: 160, 
                    className: 'sig-canvas-native' 
                  }}
                />

                <div className="canvas-actions">
                  <button onClick={handleClearSignature} className="action-button cancel">
                    Clear
                  </button>
                  <button onClick={handleSaveSignature} className="action-button save">
                    Save
                  </button>
                </div>
              </div>
            ) : signatures[currentPage] ? (
              <div className="signature-info">
                <img 
                  src={signatures[currentPage].data}
                  alt="Current Signature"
                  className="signature-thumbnail"
                />
                <div className="signature-details">
                  <p className="sig-location">Page {currentPage}</p>
                  <p className="sig-coords">({Math.round(signatures[currentPage].x)}, {Math.round(signatures[currentPage].y)})</p>
                </div>
                <button 
                  onClick={handleClearPageSignature}
                  className="action-button delete"
                >
                  ‚úï
                </button>
              </div>
            ) : (
              <div className="empty-state-panel">
                <p>‚úé Draw your signature</p>
                <p className="hint">Tap above to get started</p>
              </div>
            )}
          </div>
        </div>
      )}

      {pdfFile && (
        <div className="bottom-bar">
          <button 
            onClick={() => {
              setPdfFile(null);
              setSignatures({});
              setCurrentPage(1);
            }}
            className="bottom-button secondary"
          >
            ‚Ü∫ New PDF
          </button>
          <button 
            onClick={handleExport}
            disabled={Object.keys(signatures).length === 0}
            className="bottom-button primary"
          >
            ‚¨á Export ({Object.keys(signatures).length})
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
